import React, { useEffect, useRef, useState } from 'react';
import styled from 'styled-components';

const BackgroundContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 0;
  overflow: hidden;
  pointer-events: none;
  background-color: #0a0a0a;
`;

const FallbackBackground = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(45deg, #0a0a0a, #001100);
`;

// Background component using VANTA.js CLOUDS effect
const Background3D = () => {
  const vantaRef = useRef(null);
  const [fallback, setFallback] = useState(false);

  useEffect(() => {
    let effect = null;

    // Check if VANTA is available
    if (typeof window.VANTA !== 'undefined') {
      try {
        // Initialize VANTA CLOUDS effect
        effect = window.VANTA.CLOUDS({
          el: vantaRef.current,
          mouseControls: true,
          touchControls: true,
          gyroControls: false,
          minHeight: 200.0,
          minWidth: 200.0,
          skyColor: 0x00aa00,
          cloudColor: 0x001100,
          cloudShadowColor: 0x000000,
          speed: 0.7,
          backgroundColor: 0x0a0a0a,
        });
      } catch (error) {
        console.error('Failed to initialize VANTA effect:', error);
        setFallback(true);
      }
    } else {
      setFallback(true);
    }

    // Cleanup function
    return () => {
      if (effect) effect.destroy();
    };
  }, []); // Empty dependency array is intentional here

  return (
    <BackgroundContainer ref={vantaRef}>
      {fallback && <FallbackBackground />}
    </BackgroundContainer>
  );
};

export default Background3D;
