import React, { useRef, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

// Styled Components
const AppContainer = styled.div`
  position: relative;
  min-height: 100vh;
  font-family: 'Space Grotesk', sans-serif;
  color: white;
  overflow-x: hidden;
`;

const ParallaxBackground = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: url('https://images.unsplash.com/photo-1534972195531-d756b9bfa9f2?ixlib=rb-4.0.3&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=1080&fit=max')
        center/cover no-repeat fixed,
      url('https://images.unsplash.com/photo-1566186474485-e533ab57a675?ixlib=rb-4.0.3&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=1080&fit=max')
        center/cover no-repeat fixed,
      radial-gradient(ellipse at bottom, #1b2735 0%, #090a0f 100%);
    background-blend-mode: screen, multiply, normal;
    filter: brightness(0.5) contrast(1.2);
    animation: parallaxMove 80s infinite alternate linear;
    transform: translateZ(0);
    will-change: transform;
  }

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(
        1px 1px at 25% 30%,
        rgba(255, 255, 255, 0.4),
        transparent
      ),
      radial-gradient(1px 1px at 50% 50%, rgba(255, 255, 255, 0.2), transparent),
      radial-gradient(2px 2px at 75% 75%, rgba(255, 255, 255, 0.2), transparent);
    background-size: 100% 100%;
    animation: starsMove 60s infinite alternate linear;
    transform: translateZ(0);
    will-change: transform;
  }

  @keyframes parallaxMove {
    0% {
      background-position: 0% 0%, 0% 0%, 0% 0%;
    }
    100% {
      background-position: 0% 100%, 0% 100%, 0% 0%;
    }
  }

  @keyframes starsMove {
    0% {
      transform: translateY(0);
    }
    100% {
      transform: translateY(-20%);
    }
  }
`;

