import React, { useRef, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

// Styled Components
const AppContainer = styled.div`
  position: relative;
  min-height: 100vh;
  font-family: 'Space Grotesk', sans-serif;
  color: white;
  overflow-x: hidden;
`;

// Replaced ParallaxBackground with FireflyContainer
const FireflyContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
  overflow: hidden;
`;

const NavBar = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
  backdrop-filter: blur(10px);
  border-radius: 10px;
  background-color: rgba(0, 0, 0, 0.2);
  position: fixed;
  top: 20px;
  left: 20px;
  right: 20px;
  z-index: 100;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.3);

  @media (max-width: 768px) {
    padding: 15px;
  }
`;

const Logo = styled.div`
  font-size: 1.8rem;
  font-weight: 700;
  color: #3399ff;
  text-shadow: 0 0 10px rgba(51, 153, 255, 0.5);
`;

const NavLinks = styled.div`
  display: flex;
  gap: 2rem;

  @media (max-width: 768px) {
    gap: 1rem;
  }

  @media (max-width: 576px) {
    gap: 0.5rem;
  }
`;

const NavLink = styled.a`
  color: #3399ff;
  text-decoration: none;
  font-size: 1.2rem;
  font-weight: 500;
  transition: all 0.3s ease;
  cursor: pointer;

  &:hover {
    text-decoration: none;
    text-shadow: 0 0 8px rgba(51, 153, 255, 0.8);
    transform: translateY(-2px);
  }

  @media (max-width: 768px) {
    font-size: 1rem;
  }
`;

const ContentContainer = styled.div`
  padding: 110px 20px 50px;
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
`;

const Section = styled.section`
  min-height: 100vh;
  padding: 60px 0;
  display: flex;
  flex-direction: column;
  justify-content: center;
  scroll-margin-top: 100px;

  &:not(:last-child) {
    border-bottom: 1px solid rgba(51, 153, 255, 0.1);
  }
`;

const SectionContent = styled(motion.div)`
  padding: 40px;
