import React from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import styled from 'styled-components';
import Background3D from './components/Background3D';

const AppContainer = styled.div`
  position: relative;
  min-height: 100vh;
  z-index: 1;
  font-family: 'Space Grotesk', sans-serif;
`;

const ContentWrapper = styled.div`
  position: relative;
  z-index: 2;
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
`;

const NavBar = styled.nav`
  display: flex;
  gap: 2rem;
  margin-bottom: 40px;
  padding: 20px;
  backdrop-filter: blur(10px);
  border-radius: 10px;
  background-color: rgba(0, 0, 0, 0.2);
  position: sticky;
  top: 0;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.3);
  margin: 0 auto 40px auto;
`;

const StyledLink = styled(Link)`
  color: #00ff00;
  text-decoration: none;
  font-size: 1.2rem;
  font-weight: 500;
  transition: all 0.3s ease;

  &:hover {
    text-decoration: none;
    text-shadow: 0 0 8px rgba(0, 255, 0, 0.8);
    transform: translateY(-2px);
  }
`;

const PageContainer = styled.div`
  padding: 40px;
  background: rgba(0, 0, 0, 0.6);
  border-radius: 20px;
  max-width: 900px;
  margin: 0 auto;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(0, 255, 0, 0.1);
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.5);

  h1 {
    color: #00ff00;
    margin-bottom: 20px;
    font-size: 2.5rem;
    text-shadow: 0 0 10px rgba(0, 255, 0, 0.3);
  }

  p {
    color: #ffffff;
    font-size: 1.1rem;
    line-height: 1.7;
    margin-bottom: 20px;
  }
`;

const HomePage = () => (
  <PageContainer>
    <h1>Welcome to My Portfolio</h1>
    <p>
      I'm a developer passionate about creating beautiful interactive web
      experiences. This portfolio showcases my skills and projects with a unique
      cloud-based 3D background.
    </p>
    <p>
      Feel free to explore the different sections to learn more about me and my
      work.
    </p>
  </PageContainer>
);

const AboutPage = () => (
  <PageContainer>
    <h1>About Me</h1>
    <p>
      I'm a passionate developer with expertise in modern web technologies. I
      specialize in creating responsive, intuitive user interfaces that deliver
      engaging user experiences.
    </p>
    <p>
      My technical skills include React, Three.js, Node.js, and various other
      frontend and backend technologies. I'm constantly learning and adapting to
      new technologies to stay at the forefront of web development.
    </p>
  </PageContainer>
);

function App() {
  return (
    <AppContainer>
      <Background3D />
      <ContentWrapper>
        <NavBar>
          <StyledLink to='/'>Home</StyledLink>
          <StyledLink to='/about'>About</StyledLink>
        </NavBar>

        <Routes>
          <Route path='/' element={<HomePage />} />
          <Route path='/about' element={<AboutPage />} />
        </Routes>
      </ContentWrapper>
    </AppContainer>
  );
}

export default App;
