import React, { useEffect, useRef } from 'react';
import styled from 'styled-components';

const BackgroundContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 0;
  overflow: hidden;
  pointer-events: none;
`;

// Background component using VANTA.js
const Background3D = () => {
  const vantaRef = useRef(null);

  useEffect(() => {
    // Check if VANTA is available
    if (typeof window.VANTA !== 'undefined') {
      // Initialize VANTA effect
      const vantaEffect = window.VANTA.BIRDS({
        el: vantaRef.current,
        mouseControls: true,
        touchControls: true,
        gyroControls: false,
        minHeight: 200.0,
        minWidth: 200.0,
        scale: 1.0,
        scaleMobile: 1.0,
        backgroundColor: 0x0a0a0a,
        color1: 0x00ff00,
        color2: 0x0000ff,
        colorMode: 'lerpGradient',
        birdSize: 1.5,
        wingSpan: 30.0,
        speedLimit: 5.0,
        separation: 60.0,
        alignment: 1.0,
        cohesion: 20.0,
        quantity: 3.0,
      });

      // Cleanup function
      return () => {
        if (vantaEffect) vantaEffect.destroy();
      };
    }
  }, []);

  return <BackgroundContainer ref={vantaRef} />;
};

export default Background3D;
