import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';

const move = keyframes`
  0% {
    left: 0;
    opacity: 0;
  }
  35% {
    left: 41%;
    transform: rotate(0deg);
    opacity: 1;
  }
  65% {
    left: 59%;
    transform: rotate(0deg);
    opacity: 1;
  }
  100% {
    left: 100%;
    transform: rotate(-180deg);
    opacity: 0;
  }
`;

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const LoaderContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: #000;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  z-index: 9999;
`;

const LoadingBar = styled.div`
  position: absolute;
  width: 800px;
  height: 48px;
  left: 50%;
  top: 40%;
  margin-left: -400px;
  overflow: visible;
  user-select: none;
  cursor: default;

  @media (max-width: 768px) {
    width: 400px;
    margin-left: -200px;
  }
`;

const LoadingLetter = styled.div`
  position: absolute;
  width: 30px;
  height: 48px;
  opacity: 0;
  font-family: Helvetica, Arial, sans-serif;
  animation: ${move} 2s linear infinite;
  transform: rotate(180deg);
  color: #35c4f0;
  font-size: 36px;
  font-weight: bold;

  &:nth-child(2) { animation-delay: 0.2s; }
  &:nth-child(3) { animation-delay: 0.4s; }
  &:nth-child(4) { animation-delay: 0.6s; }
  &:nth-child(5) { animation-delay: 0.8s; }
  &:nth-child(6) { animation-delay: 1s; }
  &:nth-child(7) { animation-delay: 1.2s; }
`;

const LoadingMessage = styled.div`
  position: absolute;
  top: 30%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  color: #35c4f0;
  font-size: 3rem;
  font-weight: bold;
  text-shadow: 0 0 10px rgba(53, 196, 240, 0.5);
  opacity: 0;
  animation: ${fadeIn} 0.5s ease-out forwards;

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const WelcomeMessage = styled.div`
  position: absolute;
  top: 60%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center;
  color: #35c4f0;
  font-size: 2.5rem;
  font-weight: bold;
  opacity: 0;
  animation: ${fadeIn} 0.5s ease-out forwards;
  animation-delay: 2s;
  text-shadow: 0 0 10px rgba(53, 196, 240, 0.5);

  @media (max-width: 768px) {
    font-size: 1.8rem;
  }
`;

const Loader = () => {
  const [showWelcome, setShowWelcome] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowWelcome(true);
    }, 2000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <LoaderContainer>
      <LoadingMessage>Loading...</LoadingMessage>
      <LoadingBar>
        <LoadingLetter>H</LoadingLetter>
        <LoadingLetter>A</LoadingLetter>
        <LoadingLetter>Y</LoadingLetter>
        <LoadingLetter>A</LoadingLetter>
        <LoadingLetter>T</LoadingLetter>
        <LoadingLetter>S</LoadingLetter>
        <LoadingLetter>!</LoadingLetter>
      </LoadingBar>
      {showWelcome && (
        <WelcomeMessage>
          Welcome to Hayat's Profile
        </WelcomeMessage>
      )}
    </LoaderContainer>
  );
};

export default Loader;
